import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'
import { GetCourses } from '../../services/api';
import './home.scss';

const Home = (props) => {
    const { state } = useLocation();
    const [courses, setCourses] = useState([]);
    const [coursesID, setCoursesID] = useState([1, 2, 3]);

    useEffect(() => {
        GetCourses()
            .then(response => response.json())
            .then(data => setCourses(data.data))
    }, [])
    console.log("State: ", courses);

    const onSubmitHandler = () => {
        console.log("coursesID", coursesID);
    }
    return (
        <div className='home-page'>
            <div className="header-container">
                <h1 className='home-page__title'>Register course <span className='title-username'>{state.StudentName}</span></h1>
                <button className="profile-btn">Profile</button>
            </div>
            <form onSubmit={onSubmitHandler}>
                {
                    courses.map(course =>
                        <CourseInput course={course} coursesID={coursesID} setCoursesID={setCoursesID} key={course._ID} />
                    )}
                <div className="btn-container">
                    <button className="register-btn" type='submit' >Register</button>
                </div>
            </form>
        </div>
    )
}

const CourseInput = ({ course, coursesID, setCoursesID }) => {

    const onChangeHandler = (courseID) => {
        let coursesIDArray = [];
        // coursesIDArray = coursesID;
        console.log("coursesID1", coursesID[0]);
        // const index = coursesID.indexOf(courseID)
        // if (index > -1) {
        //     setCoursesID(coursesID.splice(index, 1))
        // } else {
        //     setCoursesID(coursesID.push(courseID))
        // }
    }
    return (
        <div className="register-container">
            <input className='course-check-box' id={course._ID} type="checkbox" value={course._ID} onChange={onChangeHandler(course._ID)} />
            <label className='course-label' htmlFor={course._ID}>{course.CourseName}</label>
        </div>
    )
}

export default Home
