import React, { useEffect, useState } from 'react'
import { GetStudentCourses } from '../../services/api'
import './profile.scss'

const Profile = () => {
    const { state } = useLocation();
    const [courses, setCourses] = useState([]);
    useEffect(() => {
        GetStudentCourses(state._ID)
            .then(response => response.json())
            .then(data => setCourses(data.data))
    }, [])
    return (
        <div className='profile-container'>
            <div className="header-container">
                <h1 className="username">Student name: <span className='username-container'></span></h1>
                <h2 className="student-id">ID: <span className='id-container'></span></h2>
            </div>
            <div className="courses-registered">
                <h1 className="courses-registered__title">Student's courses</h1>
                <h3 className='course-name' >- TMC</h3>
            </div>
        </div>
    )
}

export default Profile
