import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'
import { GetCourses } from '../../services/api';
import './home.scss';

const Home = (props) => {
    const { state } = useLocation();
    const [courses, setCourses] = useState([]);

    useEffect(() => {
        GetCourses()
            .then(response => response.json())
            .then(data => setCourses(data.data))
    }, [])
    console.log("State: ", courses);

    const onChangeHandler = () => {

    }
    return (
        <div className='home-page'>
            <div className="header-container">
                <h1 className='home-page__title'>Register course <span className='title-username'>{state.StudentName}</span></h1>
                <button className="profile-btn">Profile</button>
            </div>
            {
                courses.map(course =>
                    <CourseInput course={course} />
                )}
            <div className="btn-container">
                <button className="register-btn">Register</button>
            </div>
        </div>
    )
}

const CourseInput = ({ course }) => {
    return (
        <div className="register-container">
            <input className='course-check-box' id={course._ID} type="checkbox" />
            <label className='course-label' htmlFor="chk">{course.CourseName}</label>
        </div>
    )
}

export default Home
