import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'
import { GetCourses } from '../../services/api';
import './home.scss';

const Home = (props) => {
    const { state } = useLocation();
    const [courses, setCourses] = useState([]);
    const [coursesID, setCoursesID] = useState([1, 2, 3]);
    let selected = [];
    useEffect(() => {
        GetCourses()
            .then(response => response.json())
            .then(data => setCourses(data.data))
    }, [])
    // console.log("State: ", courses);

    const onSubmitHandler = () => {
        console.log("coursesID", coursesID);
    }
    return (
        <div className='home-page'>
            <div className="header-container">
                <h1 className='home-page__title'>Register course <span className='title-username'>{state.StudentName}</span></h1>
                <button className="profile-btn">Profile</button>
            </div>
            <form onSubmit={onSubmitHandler}>
                {
                    courses.map(course =>
                    // <CourseInput course={course} coursesID={coursesID} setCoursesID={setCoursesID} key={course._ID} />
                    {
                        const onChangeHandler = (courseID) => {

                            const index = selected.indexOf(courseID)

                            if (index > -1) {
                                setCoursesID(selected.splice(index, 1))
                            } else {
                                setCoursesID(selected.push(courseID))
                            }
                            setCoursesID(...selected)
                            // console.log("coursesID", coursesID);
                        }
                        return (
                            <div className="register-container" key={course._ID}>
                                <input className='course-check-box' id={course._ID} type="checkbox" onChange={() => onChangeHandler(course._ID)} />
                                <label className='course-label' htmlFor={course._ID}>{course.CourseName}</label>
                            </div>
                        )
                    }
                    )}
                <div className="btn-container">
                    <button className="register-btn" type='submit' >Register</button>
                </div>
            </form>
        </div>
    )
}

