                <div className="btn-container">
                    <button className="register-btn" type='submit' >Register</button>
                </div>
            </form>
        </div>
    )
}

function CourseInput({ course, coursesID, setCoursesID }) {

    const onChangeHandler = (e) => {
        const { value, checked } = e.target;
        const { coursesSelected } = coursesID

        console.log(`${value} is ${checked}`);

        if (checked) {
            setCoursesID({
                coursesSelected: [...coursesSelected, value]
            })
        } else {
            setCoursesID({
                coursesSelected: coursesSelected.filter((e) => e !== value)
            })
        }
    }
    return (
        <div className="register-container">
            <input className='course-check-box' id={course._ID} type="checkbox" value={course._ID} onChange={onChangeHandler(e)} />
            <label className='course-label' htmlFor={course._ID}>{course.CourseName}</label>
        </div>
    )
}

export default Home
